import "@testing-library/jest-dom";

describe('afterAll method test', () => {
  let originalError;
  let errorLog = [];

  beforeEach(() => {
    originalError = console.error;
    console.error = (...args) => {
      errorLog.push(...args);
      originalError(...args);
    }
  });

  afterEach(() => {
    console.error = originalError;
    errorLog = [];
  });

  afterAll(() => {
    console.error = originalError;
  });

  test('afterAll restores original console.error', () => {
    console.error('Test error message');
    expect(errorLog).toContain('Test error message');
    expect(console.error).not.toBe(originalError);

    console.error = originalError;
    
    console.error('Test error message after restore');
    expect(errorLog).toContain('Test error message after restore');
    expect(console.error).toBe(originalError);
  });

  test('console.error is restored after all tests', () => {
    console.error('Another test error message');
    expect(errorLog).toContain('Another test error message');
    expect(console.error).toBe(originalError);
  });
});
